import React from "react";
import { Link } from "react-router-dom";
import { TextField, Fab } from '@material-ui/core';
import NavigationIcon from '@material-ui/icons/Navigation';
import './Lobby.css'

function Lobby() {


  const [gameID, setGameID] = React.useState("");
  const [numClients, setNumClients] = React.useState(0);

  const handleTextChange = (event) => {
    setGameID(event.target.value);
  };

  return (
    <div className="Lobby">

      { numClients > 0 ?
        <p>
          Clients connected: {numClients}
        </p>
        :
        <p>
           No clients connected.
        </p>
      }

      <form noValidate autoComplete="off" className='input-form' >
        <TextField
          id="filled-basic"
          label="Game ID"
          color="primary"
          variant="outlined"
          onChange={handleTextChange}
        />
        <Link to={`/${gameID}`} className="join-game-button">
          <Fab
            variant="extended"
            size="large"
            color="primary"
            aria-label="add"
            className="join-game-button-inner"
          >
            <NavigationIcon className="join-game-button-icon" />
          </Fab>
        </Link>
      </form>
    </div>
  );
};

export default Lobby;
